import React, { useState } from 'react';
import { useDrag, useDrop } from 'react-dnd';
import './Home.css';

// Unique identifier for each draggable component
const ITEM_TYPE = 'ITEM';

const Home = ({ moveComponent, index }) => {
  const [{ isDragging }, drag] = useDrag({
    type: ITEM_TYPE,
    item: { index },  // Pass index so the parent can manage it
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });

  const [, drop] = useDrop({
    accept: ITEM_TYPE,
    hover: (item) => {
      if (item.index !== index) {
        moveComponent(item.index, index); // Move component to a new position
        item.index = index; // Update index of the dragged item
      }
    },
  });

  return (
    <div
      ref={(node) => drag(drop(node))}  // Combine drag and drop functionality
      className="home-container"
      style={{ opacity: isDragging ? 0.5 : 1 }}
    >
      <h2>Home</h2>
      {/* Content of Home page */}
    </div>
  );
};

export default Home;
