import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProgressTracking.css';

const ProgressTracking = () => {
  const navigate = useNavigate();

  // State for user input (for example, updating course preferences)
  const [moduleProgress, setModuleProgress] = useState({
    module1: 80,
    module2: 40,
    module3: 60,
  });

  const handleProgressChange = (module, value) => {
    setModuleProgress({
      ...moduleProgress,
      [module]: value,
    });
  };

  return (
    <div className="page-content progress-tracking">
      <button className="back-btn" onClick={() => navigate(-1)}>Back</button>

      <h2>Progress Overview</h2>

      {/* Overall Progress */}
      <div className="progress-box">
        <h3>Overall Progress</h3>
        <div className="progress-bar">
          <div className="filled" style={{ width: `${moduleProgress.module1}%` }}></div>
        </div>
        <p>{moduleProgress.module1}% Complete</p>
      </div>

      {/* Course Progress with user input */}
      <div className="progress-cards">
        <div className="progress-card">
          <h3>Module 1</h3>
          <p>{moduleProgress.module1}% completed</p>
          <input
            type="number"
            value={moduleProgress.module1}
            onChange={(e) => handleProgressChange('module1', e.target.value)}
            min="0"
            max="100"
            className="progress-input"
          />
        </div>

        <div className="progress-card">
          <h3>Module 2</h3>
          <p>{moduleProgress.module2}% completed</p>
          <input
            type="number"
            value={moduleProgress.module2}
            onChange={(e) => handleProgressChange('module2', e.target.value)}
            min="0"
            max="100"
            className="progress-input"
          />
        </div>

        <div className="progress-card">
          <h3>Module 3</h3>
          <p>{moduleProgress.module3}% completed</p>
          <input
            type="number"
            value={moduleProgress.module3}
            onChange={(e) => handleProgressChange('module3', e.target.value)}
            min="0"
            max="100"
            className="progress-input"
          />
        </div>
      </div>
    </div>
  );
};

export default ProgressTracking;
