import React, { useState } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import './App.css';

// Import your pages or components
import Home from './pages/Home';
import Goals from './pages/Goals';
import Profile from './pages/Profile';
import ProgressTracking from './pages/ProgressTracking';
import RecommendedResources from './pages/RecommendedResources';
import Feedback from './pages/Feedback';

// Reusable Drag-and-Drop Container Component
const DraggableContainer = ({ children, id }) => {
  return (
    <div className="draggable-container" id={id}>
      {children}
    </div>
  );
};

const App = () => {
  const [layout, setLayout] = useState([
    'home',
    'goals',
    'profile',
    'progressTracking',
    'recommendedResources',
    'feedback',
  ]);

  // This function would handle the logic for moving components around
  const moveComponent = (dragIndex, hoverIndex) => {
    const updatedLayout = [...layout];
    const [movedItem] = updatedLayout.splice(dragIndex, 1);
    updatedLayout.splice(hoverIndex, 0, movedItem);
    setLayout(updatedLayout);
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div className="App">
        <h1>Personalized Learning Dashboard</h1>

        <div className="dashboard">
          {layout.map((section, index) => {
            switch (section) {
              case 'home':
                return (
                  <DraggableContainer key="home" id="home">
                    <Home moveComponent={moveComponent} index={index} />
                  </DraggableContainer>
                );
              case 'goals':
                return (
                  <DraggableContainer key="goals" id="goals">
                    <Goals moveComponent={moveComponent} index={index} />
                  </DraggableContainer>
                );
              case 'profile':
                return (
                  <DraggableContainer key="profile" id="profile">
                    <Profile moveComponent={moveComponent} index={index} />
                  </DraggableContainer>
                );
              case 'progressTracking':
                return (
                  <DraggableContainer key="progressTracking" id="progressTracking">
                    <ProgressTracking moveComponent={moveComponent} index={index} />
                  </DraggableContainer>
                );
              case 'recommendedResources':
                return (
                  <DraggableContainer key="recommendedResources" id="recommendedResources">
                    <RecommendedResources moveComponent={moveComponent} index={index} />
                  </DraggableContainer>
                );
              case 'feedback':
                return (
                  <DraggableContainer key="feedback" id="feedback">
                    <Feedback moveComponent={moveComponent} index={index} />
                  </DraggableContainer>
                );
              default:
                return null;
            }
          })}
        </div>
      </div>
    </DndProvider>
  );
};

export default App;
